import React, { useState } from 'react';
import { api } from '../services/api';

const Register = ({ onRegister, onSwitchToLogin }) => {
  const [formData, setFormData] = useState({
    nombre: '',
    correo: '',
    password: '',
    confirmPassword: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Validar que las contrase√±as coincidan
    if (formData.password !== formData.confirmPassword) {
      setError('Las contrase√±as no coinciden');
      return;
    }

    setLoading(true);

    try {
      // Enviar solo los datos necesarios con rol fijo de ESTUDIANTE
      const registroData = {
        nombre: formData.nombre,
        correo: formData.correo,
        password: formData.password,
        rol: 'ESTUDIANTE'
      };

      const response = await api.post('/usuarios/registro', registroData);
      if (response.data.success) {
        alert('‚úÖ Registro exitoso. Por favor inicia sesi√≥n.');
        onRegister();
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Error al registrar');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center px-4 py-12">
      <div className="max-w-2xl w-full">
        <div className="text-center mb-8">
          <div className="text-6xl mb-4">üéì</div>
          <h2 className="text-3xl font-bold text-gray-900">Registro de Estudiante</h2>
          <p className="text-gray-600 mt-2">Sistema de Gesti√≥n de Turnos - Laboratorios</p>
        </div>

        <div className="card p-8">
          <form onSubmit={handleSubmit} className="space-y-6">
            {error && (
              <div className="bg-red-50 border border-red-200 rounded-lg p-4 text-red-600 text-sm">
                {error}
              </div>
            )}

            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">
                Nombre Completo *
              </label>
              <input
                type="text"
                value={formData.nombre}
                onChange={(e) => setFormData({...formData, nombre: e.target.value})}
                className="input-field"
                placeholder="Ej: Juan P√©rez Garc√≠a"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">
                Correo Electr√≥nico *
              </label>
              <input
                type="email"
                value={formData.correo}
                onChange={(e) => setFormData({...formData, correo: e.target.value})}
                className="input-field"
                placeholder="ejemplo@universidad.edu.co"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">
                Contrase√±a * (m√≠nimo 8 caracteres)
              </label>
              <input
                type="password"
                value={formData.password}
                onChange={(e) => setFormData({...formData, password: e.target.value})}
                className="input-field"
                minLength="8"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">
                Confirmar Contrase√±a *
              </label>
              <input
                type="password"
                value={formData.confirmPassword}
                onChange={(e) => setFormData({...formData, confirmPassword: e.target.value})}
                className="input-field"
                minLength="8"
                required
              />
              {formData.confirmPassword && formData.password !== formData.confirmPassword && (
                <p className="text-xs text-red-600 mt-1">
                  ‚ö†Ô∏è Las contrase√±as no coinciden
                </p>
              )}
              {formData.confirmPassword && formData.password === formData.confirmPassword && (
                <p className="text-xs text-green-600 mt-1">
                  ‚úì Las contrase√±as coinciden
                </p>
              )}
            </div>

            <button
              type="submit"
              disabled={loading}
              className="w-full btn-primary text-lg py-3"
            >
              {loading ? 'Registrando...' : 'Registrarse'}
            </button>

            <div className="text-center">
              <button
                type="button"
                onClick={onSwitchToLogin}
                className="text-lab-blue hover:text-blue-700 text-sm font-medium"
              >
                ¬øYa tienes cuenta? Inicia sesi√≥n aqu√≠
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Register;
